Ext.define('MEC_App.controller.BusinessIndicatorsController', {
    extend: 'Ext.app.Controller',

    config: {
        control: {
            "panel#BusinessIndicatorsView": {
                initialize: 'onBusinessIndicatorsViewInitialize'
            }
        }
    },

    onBusinessIndicatorsViewInitialize: function(component, eOpts) {
        var view = component;//me.getMyBusinessView();


        Ext.AnimationHelper.ShowLoading();

        var view = component;


        Ext.Ajax.request({
            url : Ext.Global.GetConfig('CMSWSUrl')+ '/QuarterlyBusinessReport/GetAllQuarterlyBusinessReportCategories?culture='+ Ext.Global.LanguageFlag +'&pageIndex=0&pageSize=20',
            method : 'Get',
            success : function (response) {

                var json = Ext.util.JSON.decode(response.responseText);

                //view.setData(json);
                view.links = json;


                var valueField,
                    busRecord,
                    busReg,
                    valueType;
                if(Ext.Global.LanguageFlag==='ar'){
                    valueField =  'NameAr';
                    busRecord = 'السجلات التجارية';
                    busReg = 'الرخص التجارية';
                }
                else{
                    valueField = 'Name';
                    busRecord = 'business records';
                    busReg = 'commercial permit';
                }





                if(!view.getData()){// initial run
                    view.down('#hiddenCatID').setValue(json[0].Id);
                    view.down('#txtFilter').setValue(json[0][valueField]);

                    if(json[0][valueField].toLowerCase().indexOf(busRecord) < 7 && json[0][valueField].toLowerCase().indexOf(busRecord) > -1){
                        valueType = 1;
                    }
                    else if(json[0][valueField].toLowerCase().indexOf(busReg) < 7 && json[0][valueField].toLowerCase().indexOf(busReg) > -1){
                        valueType = 2;
                    }
                    else{
                        valueType = 3;
                    }
                }
                else{ // selected key is pushed from the select view
                    view.down('#hiddenCatID').setValue(view.getData().Id);
                    view.down('#txtFilter').setValue(view.getData().Title);

                    var valueName = view.getData().Title;

                    if(valueName.toLowerCase().indexOf(busRecord) < 7 && valueName.toLowerCase().indexOf(busRecord) > -1){
                        valueType = 1;
                    }
                    else if(valueName.toLowerCase().indexOf(busReg) < 7 && valueName.toLowerCase().indexOf(busReg) > -1){
                        valueType = 2;
                    }
                    else{
                        valueType = 3;
                    }
                }

                view.down('#hiddenCatType').setValue(valueType);

            }
        });






    }

});